import update from "immutability-helper";
import { useCallback, useState, useEffect } from "react";
import { Card } from "./Card.js";

const CardList = () => 
  {
    const [isMounted, setIsMounted] = useState(false)
    const [cards, setCards] = useState([]);

    const moveCard = useCallback((dragIndex, hoverIndex) => {
      setCards((prevCards) =>
        update(prevCards, {
          $splice: [
            [dragIndex, 1],
            [hoverIndex, 0, prevCards[dragIndex]]
          ]
        })
      );
    }, []);

    const deleteCard = (id) =>{
      setCards((prevCards) => {
        return prevCards.filter(card => card.id !== id)

      });

    }

    const startSprint = () =>{
      console.log(cards)
      setCards((prevCards) => {
        let tempCards = [...prevCards];
        let tempItem = tempCards.shift()
        tempCards.push(tempItem)

        return tempCards;

      });

    }

    const startS = () =>{
      console.log(cards)
    }

    const skipTurn = () =>{
      setCards((prevCards) => {
        let tempCards = [...prevCards];
        [tempCards[0], tempCards[tempCards.length - 1]] = [tempCards[tempCards.length - 1], tempCards[0]];

          return tempCards;

      });

    }

    const renderCard = useCallback((card, index) => {
      return (
        <Card
        key={card.id}
        index={index}
        id={card.id}
        text={card.text}
        moveCard={moveCard}
        deleteCard={deleteCard}
        startSprint={startSprint}
        startS={startS}

        />
      );
    }, []);

    const fetchData = async () => {
      const response = await  fetch('https://standup-6faab-default-rtdb.firebaseio.com/names/-NHQ1SUNDMiwAbGD7rq6.json');

        const data = await response.json();
      if (data!=null){
        setCards(data)
        setIsMounted(true)
      }
    }



    useEffect(() => {

      if (isMounted){
        const response =  fetch('https://standup-6faab-default-rtdb.firebaseio.com/names/-NHQ1SUNDMiwAbGD7rq6.json', {
        method: 'PUT',
        body: JSON.stringify(cards),
        headers: {
          'Content-Type': 'application/json'
        }
      });
    }

    },[cards])


    useEffect(() => {


      fetchData()

      },[]);


    const submitHandler = (event) =>{
      event.preventDefault()
      let id = 1
      if (cards.length>0){
        id=cards[cards.length - 1].id
      }
      setCards((prevCards) => {
        return [...prevCards, {"id":id+1,"text":event.target.firstName.value}]

      });
    }



    return (
      <div>
        <h1>Sprint lead tracker</h1>
        {cards&&<div className="NameList">{cards.map((card, i) => renderCard(card, i))}</div>}
        {isMounted && <form onSubmit={submitHandler}>
        <label>Add new name:</label>
        <input id="firstName" name="firstName"
      />

        <div>
          <button type="submit">
            Add
          </button>
        </div>
      </form>}
      </div>
    );
  }


export default CardList;
