import update from "immutability-helper";
import { useCallback, useState, useEffect } from "react";
import { Card } from "./Card.js";

const CardList = (props) => 
  {

    const date = new Date();
    const today = `${date.getMonth()+1}/${date.getDate()}/${date.getFullYear()}`;
    let rotDates = []

    const [isMounted, setIsMounted] = useState(false)
    const [cards, setCards] = useState([]);
    const [enteredName, setEnteredName] = useState('')
    const [enteredQuestion, setEnteredQuestion] = useState('')


    const deleteCard = (id) =>{
      setCards((prevCards) => {
        let data = prevCards.filter(card => card.id !== id)
        if (updateCards(data)){
          return data
        }
        else{
          prevCards
        }

      });

    }


    const moveCard = useCallback((dragIndex, hoverIndex) => {
      setCards((prevCards) =>{

        let data = update(prevCards, {
          $splice: [
            [dragIndex, 1],
            [hoverIndex, 0, prevCards[dragIndex]]
          ]
        })

        if (updateCards(data)){
          return data
        }
        else{
          prevCards
        }

    });
    }, []);


    const startSprint = () =>{
      setCards((prevCards) => {
        let tempCards = [...prevCards];
        let tempItem = tempCards.shift()
        tempItem.date = today
        console.log(tempItem)
        tempCards.push(tempItem)

        if (updateCards(tempCards)){
          return tempCards
        }
        else{
          prevCards
        }

      });

    }


    const fetchData = async () => {
        const response = await  fetch('https://standup-6faab-default-rtdb.firebaseio.com/names.json');

        const data = await response.json();

        if (data!=null && response.ok){
          setCards(data)
        }
        setIsMounted(true)

      }


    const updateCards = async (data) =>{
      const response =  await fetch('https://standup-6faab-default-rtdb.firebaseio.com/names.json', {
        method: 'PUT',
        body: JSON.stringify(data),
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (response.ok){
        return true
      }
      else{
        return false
      }
    }


    const submitHandler = (event) =>{
      event.preventDefault()
      let id = 1
      if (cards.length>0){
        id=cards[cards.length - 1].id
      }
      setCards((prevCards) => {
        let data = [...prevCards, {"id":id+1,"text":enteredName, "date":null}]
        if (updateCards(data)){
          return data
        }
        else{
          prevCards
        }

      });

      setEnteredName('')
    }


    const addQuestion = async (event) =>{
      event.preventDefault()

      const response =  await fetch('https://standup-6faab-default-rtdb.firebaseio.com/questions.json', {
        method: 'POST',
        body: JSON.stringify({"question":enteredQuestion}),
        headers: {
          'Content-Type': 'application/json'
        }
      });

      setEnteredQuestion('')
    }


    const setUpDates = () => {

      let d = new Date();
  var day = d.getDay(),
      diff = d.getDate() - day + (day == 0 ? -6:1); // adjust when day is sunday
  console.log(new Date(d.setDate(diff)));


      let curr = new Date; // get current date
      let first = curr.getDate() - curr.getDay(); // First day is the day of the month - the day of the week
      let last = first + 6; // last day is the first day + 6
      let firstday = new Date(curr.setDate(first));
      let lastday = new Date(curr.setDate(last));
      let start = (firstday.getMonth()+1)+"/"+firstday.getDay()
      let end = (lastday.getMonth()+1)+"/"+lastday.getDay()
      let currDate = start+" - "+end

      rotDates.push(currDate)
      console.log(curr)
      console.log(curr+6)

      for (var i = 1; i <10; i++) {
        first = last+1
        last = first + 6; // last day is the first day + 6
        firstday = new Date(curr.setDate(first));
        lastday = new Date(curr.setDate(last));
        start = (firstday.getMonth()+1)+"/"+firstday.getDate()
        end = (lastday.getMonth()+1)+"/"+lastday.getDate()
        currDate = start+" - "+end
        rotDates.push(currDate)
      }

    }


    const nameInputChangeHandler = (event) => {
      setEnteredName(event.target.value);
    };


    const questionInputChangeHandler = (event) => {
      setEnteredQuestion(event.target.value);
    };


    useEffect(() => {

      fetchData()
      setUpDates()

      },[]);


    const renderCard = useCallback((card, index) => {
      console.log(index)
      console.log(rotDates[index])
      return (
        <Card
        key={card.id}
        index={index}
        id={card.id}
        text={card.text}
        moveCard={moveCard}
        deleteCard={deleteCard}
        startSprint={startSprint}
        date={card.date}
        rotDate={rotDates[index]}

        />
      );
    }, []);



    return (
      <div>
        <h1>Sprint lead tracker</h1>
        {isMounted && <div>
        {cards&&<div className="NameList">{cards.map((card, i) => renderCard(card, i))}</div>}
        <form onSubmit={submitHandler}>
        <div className="newCardInput">
        <label htmlFor="name">Add new name:</label>
        <input
          type='text'
          id='name'
          onChange={nameInputChangeHandler}
          value={enteredName}
        />

          <button type="submit">
            Add
          </button>
          </div>
      </form>
      <form onSubmit={addQuestion}>
        <div className="newCardInput">
        <label htmlFor="name">Add new question:</label>
        <input
          type='text'
          id='name'
          onChange={questionInputChangeHandler}
          value={enteredQuestion}
        />

          <button type="submit">
            Add new question
          </button>
          </div>
      </form>
      </div>}
      </div>
    );
  }


export default CardList;
