import update from "immutability-helper";
import { useCallback, useState, useEffect } from "react";
import { Card } from "./Card.js";
const style = {
  width: 400
};
const Container = () => 
  {
    const [count, setCount] = useState(0)
    const [cards, setCards] = useState([]);
    const moveCard = useCallback((dragIndex, hoverIndex) => {
      setCards((prevCards) =>
        update(prevCards, {
          $splice: [
            [dragIndex, 1],
            [hoverIndex, 0, prevCards[dragIndex]]
          ]
        })
      );
    }, []);

    const deleteCard = (id) =>{
      console.log(id)
      setCards(cards.filter((_, i) => i === id))
    }

    const renderCard = useCallback((card, index) => {
      return (
        <Card
        key={card.id}
        index={index}
        id={card.id}
        text={card.text}
        moveCard={moveCard}
        deleteCard={deleteCard}

        />
      );
    }, []);

    useEffect(() => {


    const fetchData = async () => {
      const response = await  fetch('https://standup-6faab-default-rtdb.firebaseio.com/names/-NGx9wR38FtnfXFhQeux.json');

        const data = await response.json();
        console.log(data)
      if (data!=null){
        setCards(data)
      }



    }


    fetchData()


    },[]);

    useEffect(() => {

      if (count>0){
        const response =  fetch('https://standup-6faab-default-rtdb.firebaseio.com/names/-NGx9wR38FtnfXFhQeux.json', {
        method: 'PUT',
        body: JSON.stringify(cards),
        headers: {
          'Content-Type': 'application/json'
        }
      });
      console.log(cards)
    }

    else{
      setCount(1)
    }

    },[cards])

    const submitHandler = (event) =>{
      event.preventDefault()
      let id = 1
      if (cards.length>0){
        id=cards[cards.length - 1].id
      }
      setCards(cards => [...cards, {"id":id+1,"text":event.target.firstName.value}])
    }



    return (
      <div>
        {cards&&<div style={style}>{cards.map((card, i) => renderCard(card, i))}</div>}
        <form onSubmit={submitHandler}>
        <label>Add new name:</label>
        <input id="firstName" name="firstName"
      />

        <div>
          <button type="submit">
            Add
          </button>
        </div>
      </form>
      </div>
    );
  }


export default Container;
