import React, { useState } from "react";

function App() {
  // Use the useState hook to create a state variable to store the list of cards
  const [cards, setCards] = useState([]);

  // Create a function to add a card to the list
  const addCard = (title, description) => {
    setCards([...cards, { title, description }]);
  };

  // Create a function to remove a card from the list
  const removeCard = index => {
    setCards(cards.filter((_, i) => i !== index));
  };

  return (
    <div>
      <h1>Trello Cards</h1>
      <div>
        <input type="text" placeholder="Enter a title" />
        <input type="text" placeholder="Enter a description" />
        <button type="button" onClick={() => addCard(/* add title and description here */)}>
          Add card
        </button>
      </div>
      <ul>
        {cards.map((card, index) => (
          <li key={card.title}>
            <h2>{card.title}</h2>
            <p>{card.description}</p>
            <button type="button" onClick={() => removeCard(index)}>
              Remove card
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
