import update from "immutability-helper";
import { useCallback, useState, useEffect } from "react";
import { Card } from "./Card.js";
const style = {
  width: 400
};
const Container = () => {
  {
    const [cards, setCards] = useState([
      {
        id: 1,
        text: "Write a cool JS library"
      },
      {
        id: 4,
        text: "John"
      },
      {
        id: 2,
        text: "Make it generic enough"
      },
      {
        id: 3,
        text: "Write README"
      },
      {
        id: 5,
        text:
          "Spam in Twitter and IRC to promote it (note that this element is taller than the others)"
      },
      {
        id: 6,
        text: "???"
      },
      {
        id: 7,
        text: "PROFIT"
      }
    ]);
    const moveCard = useCallback((dragIndex, hoverIndex) => {
      setCards((prevCards) =>
        update(prevCards, {
          $splice: [
            [dragIndex, 1],
            [hoverIndex, 0, prevCards[dragIndex]]
          ]
        })
      );
    }, []);
    const renderCard = useCallback((card, index) => {
      return (
        <Card
          key={card.id}
          index={index}
          id={card.id}
          text={card.text}
          moveCard={moveCard}
        />
      );
    }, []);

    useEffect(() => {


    const fetchData = async () => {
      const response = await  fetch('https://standup-6faab-default-rtdb.firebaseio.com/names/-NGx9wR38FtnfXFhQeux.json');
      const data = await response.json();
      console.log(data)
    }


    fetchData()


    },[]);

    const sendData = () =>{
      const response = fetch('https://standup-6faab-default-rtdb.firebaseio.com/names/-NGx9wR38FtnfXFhQeux.json', {
      method: 'PUT',
      body: JSON.stringify(cards),
      headers: {
        'Content-Type': 'application/json'
      }
    });
    }

    return (
      <div>
        <div style={style}>{cards.map((card, i) => renderCard(card, i))}</div>
        <button onClick={sendData}>Test</button>
      </div>
    );
  }
};

export default Container;
