import React, { useState } from "react";

function App() {
  // Use the useState hook to create a state variable to store the list of lists
  const [lists, setLists] = useState([]);

  // Create a function to add a list to the board
  const addList = name => {
    setLists([...lists, { name, cards: [] }]);
  };

  // Create a function to remove a list from the board
  const removeList = index => {
    setLists(lists.filter((_, i) => i !== index));
  };

  // Create a function to add a card to a list
  const addCard = (listIndex, title, description) => {
    const newLists = [...lists];
    newLists[listIndex].cards.push({ title, description });
    setLists(newLists);
  };

  // Create a function to remove a card from a list
  const removeCard = (listIndex, cardIndex) => {
    const newLists = [...lists];
    newLists[listIndex].cards = newLists[listIndex].cards.filter((_, i) => i !== cardIndex);
    setLists(newLists);
  };

  return (
    <div>
      <h1>Trello Board</h1>
      <div>
        <input type="text" placeholder="Enter a list name" />
        <button type="button" onClick={() => addList(/* add list name here */)}>
          Add list
        </button>
      </div>
      <div>
        {lists.map((list, listIndex) => (
          <div key={list.name}>
            <h2>{list.name}</h2>
            <ul>
              {list.cards.map((card, cardIndex) => (
                <li key={card.title}>
                  <h3>{card.title}</h3>
                  <p>{card.description}</p>
                  <button type="button" onClick={() => removeCard(listIndex, cardIndex)}>
                    Remove card
                  </button>
                </li>
              ))}
            </ul>
            <div>
              <input type="text" placeholder="Enter a card title" />
              <input type="text" placeholder="Enter a card description" />
              <button type="button" onClick={() => addCard(listIndex, /* add card title and description here */)}>
                Add card
              </button>
            </div>
            <button type="button" onClick={() => removeList(listIndex)}>
              Remove list
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}



export default App
