import {Outlet, Link } from "react-router-dom";
import "../styles.css";

const SwitchPages = (props) =>{
    const activatePicker = () =>{
        props.chooseStandup(false)
        props.choosePicker(true)

    }

    const activateStandup = () =>{
        props.choosePicker(false)
        props.chooseStandup(true)
    }


    return(
        <div>
            <nav className="SwitchPages">
        <div className="SwitcherButtons">
          <div className={`buttonSwitch ${props.pickerStatus ? "active" : "inactive"}`} onClick={activatePicker}>
            <Link to="/">Random Picker</Link>
          </div>
          <div className={`buttonSwitch ${props.standupStatus ? "active" : "inactive"}`} onClick={activateStandup}>
            <Link to="/standup">Standup Lead</Link>
          </div>
        </div>
      </nav>
      <Outlet />
        </div>
    )
}

export default SwitchPages
